import {  useDispatch, useSelector } from 'react-redux'
import style from './CategoryDetails.module.scss'
import { addToCart } from "../../store/CartSlice";
import { useEffect, useState } from 'react';
import { getProductsOfCategory } from '../../store/CatgoriesSlice';

export default function CategoryDetails() {
    const {products,categories} = useSelector(state=>state.categories)
    const dispatch = useDispatch();
    let [modalData, setModalData] = useState("");
    let getData = (state) => {
      setModalData(state);
    };
    useEffect(() => {
      categories.forEach(item => {
        
      });
      dispatch(getProductsOfCategory(`${categories.id}`))
    }, [dispatch,categories])
    
  return (
    <div className={`${style.details}`}>
        <div className="container">
            <div className="row">
                {products.map((item)=>
                <div className="col-lg-3 col-md-6" key={item.id}>
                <div className={`${style.item}`}>
                  <img
                    src={item.images[0]}
                    alt=""
                    className="w-100"
                    onMouseOver={(e) => (e.currentTarget.src = item.images[1])}
                    onMouseOut={(e) => (e.currentTarget.src = item.images[0])}
                  />
                  <h3 className={`${style.Itemtitle}`}>{item.title}</h3>
                  <button className={`${style.myBtn} btn btn-danger`}>
                    {item.price} $
                  </button>
                  <div className={`${style.ItemBtn}`}>
                    <i
                      className="fa-regular fa-eye"
                      data-bs-toggle="modal"
                      data-bs-target="#staticBackdrop"
                      onClick={() => getData(item)}
                    ></i>
                    <button className={`${style.Btn} btn btn-danger`} onClick={() => dispatch(addToCart(item))}>
                      Add To Cart
                    </button>
                    <i className="fa-regular fa-heart"></i>
                  </div>
                </div>
                <div
                    className={`${style.Modal} modal fade`}
                    id="staticBackdrop"
                    data-bs-backdrop="static"
                    data-bs-keyboard="false"
                    tabIndex={-1}
                    aria-labelledby="staticBackdropLabel"
                    aria-hidden="true"
                  >
                    <div className="modal-dialog">
                      <div className="modal-content">
                        <div className="modal-header">
                          <h1
                            className={`${style.modalTitle} modal-title fs-5`}
                            id="staticBackdropLabel"
                          >
                            {modalData.title}
                          </h1>
                          <button
                            type="button"
                            className="btn-close"
                            data-bs-dismiss="modal"
                            aria-label="Close"
                          />
                        </div>
                        <div className={`${style.body} modal-body`}>
                          <img
                            src={modalData.images}
                            alt="productImg"
                            className={`${style.modalImg} w-100`}
                          />
                          <button
                            className={`${style.modalPrice} btn btn-danger`}
                          >
                            {modalData.price} $
                          </button>
                          <p className="text-muted">{modalData.description}</p>
                        </div>
                        <div className="modal-footer">
                          <button
                            type="button"
                            className={`${style.modalClose} btn btn-secondary`}
                            data-bs-dismiss="modal"
                          >
                            Close
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
              </div>
                )}   
            </div>
        </div>
    </div>
   
  )
}
