import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { filterByPrice, getProducts } from "../../store/ProductsSlice";
import style from "./Products.module.scss";

export default function Products() {
  const { products } = useSelector((state) => state.products);
  let [modalData, setModalData] = useState("");
  const [filteredProducts, setfilteredProducts] = useState(products);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getProducts(1));
  }, [dispatch]);
  useEffect(() => {
    setfilteredProducts(products);
  }, [products]);

  let getData = (state) => {
    setModalData(state);
  };
  return (
    <>
      <div className={`${style.products} py-5`}>
        <div className="container">
          <div className={`${style.title}`}>
            <h2>Products</h2>
            <p>Trendy</p>
          </div>
          <div className="row py-3">
            <div className="col-lg-3">
              <div className={`${style.filters} py-5`}>
                <h4>Price</h4>
                <div className={`${style.range}`}>
                  <input
                    type="range"
                    min={0}
                    max={1500}
                    onChange={(e) => {
                      setfilteredProducts(
                        products.filter((item) => item.price > e.target.value)
                      );
                    }}
                    className={`${style.filtersRangr} form-control`}
                  />
                  {/* <input type="range" min={0}
            max={1500} onChange={(e) => dispatch(filterByPrice(e.target.value))} className={`${style.filtersRangr2} form-control`} /> */}
                </div>
              </div>
            </div>
            {filteredProducts.slice(0, 16).map((item) => (
                
                <div className="col-lg-3 col-md-4">
                  <div className={`${style.item}`}>
                    <img
                      src={item.images[0]}
                      alt=""
                      className="w-100"
                      onMouseOver={(e) =>
                        (e.currentTarget.src = item.images[1])
                      }
                      onMouseOut={(e) => (e.currentTarget.src = item.images[0])}
                    />
                    <h3 className={`${style.Itemtitle}`}>{item.title}</h3>
                    <button className={`${style.myBtn} btn btn-danger`}>
                      {item.price} $
                    </button>
                    <div className={`${style.ItemBtn}`}>
                      <i
                        className="fa-regular fa-eye"
                        onClick={() => getData(item)}
                        data-bs-toggle="modal"
                        data-bs-target="#staticBackdrop"
                      ></i>
                      <button className={`${style.Btn} btn btn-danger`}>
                        Add To Cart
                      </button>
                      <i className="fa-regular fa-heart"></i>
                    </div>
                  </div>
                </div>
      
                
                
             
            ))}
          </div>
        </div>
      </div>
    </>
  );
}
