import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";




export const getProducts = createAsyncThunk('products/getProducts',async(num,thunkAPI)=>{
    const {rejectWithValue} = thunkAPI;

    try {
        let {data} = await axios.get(`https://asos2.p.rapidapi.com/products/v2/list`,
        {
            
              params: {
                store: 'US',
                offset: '0',
                categoryId: '4209',
                limit: '48',
                country: 'US',
                sort: 'freshness',
                currency: 'USD',
                sizeSchema: 'US',
                lang: 'en-US'
              },
             
            });
            console.log(data);
            return data
        
    } catch (error) {
        return rejectWithValue(error.message);  
    }

})




 const productsSlice = createSlice({
    name:'products',
    initialState:{products:[],error:null,isLoading:false},
    reducers:{
        increasePage:(state, action)=>{
            state.num+=1
            console.log(state.num+=1);
        },
        decreasePage:(state, action)=>{
            state.num-=1
        },
    },
    extraReducers:(builder)=>{
        builder.addCase(getProducts.pending,(state,action)=>{
            state.isLoading = true
            state.error = null;
        })
        builder.addCase(getProducts.fulfilled,(state,action)=>{
            state.isLoading = false;
            state.products =action.payload;
            state.error = null;

        })
        builder.addCase(getProducts.rejected,(state,action)=>{
            state.isLoading = false;
            state.error = action.payload; 
        })
    }

 })


 export default productsSlice.reducer
 export const {increasePage,decreasePage,filterByPrice} = productsSlice.actions
