import { useState } from 'react'
import { useDispatch } from 'react-redux'
import { getProducts } from '../../store/ProductsSlice'
import style from './Pagination.module.scss'

export default function Pagination({num}) {
let dispatch = useDispatch()
 let [globalPage, setglobalPage] = useState(0)
    let pageNumber = new Array(5).fill(1).map((e,index)=>index+1)

    const perv = ()=>{
      dispatch(getProducts(num))
      setglobalPage(num)
      globalPage--
    }
    const next = ()=>{
      dispatch(getProducts(num))
      setglobalPage(num)
      globalPage++
    }

  return (
 <nav aria-label="Page navigation example" className={`${style.pagination} d-flex justify-content-center py-3`}>
  <ul className="pagination">
  <li class="page-item">
      <span className="page-link" aria-label="Previous" onClick={perv}>
        <span aria-hidden="true">&laquo;</span>
      </span>
    </li>
    {pageNumber.map((num,index)=>
    <li className="page-item" onClick={()=>dispatch(getProducts(num))} key={index}><span className="page-link" >{num}</span></li>)}
     <li class="page-item">
      <span class="page-link" href="#" aria-label="Next">
        <span aria-hidden="true">&raquo;</span>
      </span>
    </li>
  </ul>
</nav>

  )
}
