import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, NavLink } from 'react-router-dom';
import { getCategories, getProductsOfCategory } from '../../store/CatgoriesSlice';
import style from './DropDown.module.scss'

export default function DropDown() {
    const dispatch = useDispatch();
    const [isOpen,setisOpen]= useState(false)
    let {categories} = useSelector(state=> state.categories)

    useEffect(() => {
        dispatch(getCategories())
    
      }, [dispatch]);

      const toggle =()=>{
        setisOpen(!isOpen)
      }
  return (
    <div className={`${style.dropDown}`}>
        <div className={`${style.header}`}>
            <h6>Categories</h6>
            <i className='fa fa-bars' onClick={toggle}></i>
        </div>
        <div className={`${style.body}`} style={{ height: isOpen ? "60vh" : "0px", transition: "all 0.5s ease" }}>
            {categories.slice(0,5).map((item)=>
            <NavLink to={`categories/${item.id}`} key={item.id} style={{display: isOpen? "block" :"none"}} onClick={()=>dispatch(getProductsOfCategory(item.id))} className={({ isActive }) => isActive ? " nav-link secBG" : "nav-link"}>
                <div className={`${style.item}`}>
               <i className="fa-solid fa-store"></i>
                <h6 >{item.name}</h6>
                </div>
                </NavLink>
            )}

        </div>

    </div>
  )
}
